class Analytics extends Component {
  constructor() {
    super();
    this.state = {
      clicked: []
    };
    this.handleClick = this.handleClick.bind(this);
    this.handleOutsideClick = this.handleOutsideClick.bind(this);
  }

  componentWillMount() {
    document.addEventListener("click", this.handleOutsideClick, false);
  }

  componentWillUnmount(){
    document.removeEventListener("click", this.handleOutsideClick, false);
  }
  handleClick(index,e) {
    let clicked=this.state.clicked;
    clicked[index]=!clicked[index];
    this.setState({clicked: clicked});
  }
  handleOutsideClick(event){
    if (!this.refs.megaMenu.contains(event.target)) {
      this.setState({
        clicked: []
      });
    } 
  }
  render() {
    return (
      <div className="container">
        <div className="menu-bar">

          <div className="menu-bar-item" ref="megaMenu">
            <a className="menu-bar-link" href="#" onClick={this.handleClick.bind(this,0)}>First Menu</a>
            <div className={"mega-menu"+" "+this.state.clicked[0]}>
              <div className="mega-menu-content">
                <p>First Menu</p>
              </div>
            </div>
          </div>

          <div className="menu-bar-item" ref="megaMenu">
            <a className="menu-bar-link" href="#" onClick={this.handleClick.bind(this,1)}>Second Menu</a>
            <div className={"mega-menu"+" "+this.state.clicked[1]}>
              <div className="mega-menu-content">
                <p>Second Menu</p>
              </div>
            </div>
          </div>

          <div className="menu-bar-item" ref="megaMenu">
            <a className="menu-bar-link" href="#" onClick={this.handleClick.bind(this,2)}>Third Menu</a>
            <div className={"mega-menu"+" "+this.state.clicked[2]}>
              <div className="mega-menu-content">
                <p>Third Menu</p>
              </div>
            </div>
          </div>

          <div className="menu-bar-item" ref="megaMenu">
            <a className="menu-bar-link" href="#" onClick={this.handleClick.bind(this,3)}>Fourth Menu</a>
            <div className={"mega-menu"+" "+this.state.clicked[3]}>
              <div className="mega-menu-content">
                <p>Fourth Menu</p>
              </div>
            </div>
          </div>

        </div>
      </div>
    );
  }
}
export default Analytics;
